mapper.select:
 
		SELECT 
		
		from ans_card t1
		where ANS_ANSWER != ANS_MY_ANSWER
		and t.ID = #{id}
		and t.ANS_TITLE = #{ansTitle}
		and t.ANS_OPTION = #{ansOption}
		and t.ANS_ANSWER = #{ansAnswer}
		and t.ANS_MY_ANSWER = #{ansMyAnswer}
		and t.ANS_DESP = #{ansDesp}
		and t.ANS_TYPE = #{ansType}
		and t.TOPIC_DICT_ID = #{topicDictId}
		and t.TOPIC_MAIN_ID = #{topicMainId}
		and t.ANS_ID = #{ansId}
		
		ORDER BY t1.TOPIC_DICT_ID
